import "./post.css"

export default function posts() {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    fetch("http://localhost:9292/games")
      .then((r) => r.json())
      .then((posts) => setPosts(posts));
  }, []);
  return (
    <section>
    {games.map((game) => (
      <PostsItem key={posts.id} posts={posts} />
    ))}
  </section>
  )
}





